var https = require('https');
var http = require('http');

class HttpClient {
  constructor(options) {
    this.port = options.port || 443;
    this.https = options.https || https;
    this.http =  options.http || http;
    this.headers = {
        'Content-Type': 'application/x-www-form-urlencoded',
        'Accept': 'application/json'
    };
    this.logger = options.logger;

    if(options.userAgent) {
      this.headers['User-Agent'] = options.userAgent;
    }
  }

  request(endpoint, method, callback) {
    if (typeof method == 'function') {
        callback = method;
        endpoint.method = endpoint.method || 'GET';
    }
    else if (typeof method !== 'undefined') {
      	endpoint.method = method;
    }

    if (endpoint.method == 'POST' || endpoint.method == 'DELETE') {
        // TODO: verify the following fix is required
        // Fix broken due ot 411 Content-Length error now sent by Nexmo API
        // PL 2016-Sept-6 - commented out Content-Length 0
        // headers['Content-Length'] = 0;
    }
    var options = {
        host: endpoint.host?endpoint.host:'rest.nexmo.com',
        port: this.port,
        path: endpoint.path,
        method: endpoint.method,
        headers: Object.assign({}, this.headers)
    };

    // Allow existing headers to be overridden
    // Allow new headers to be added
    if(endpoint.headers) {
      Object.keys(endpoint.headers).forEach(function(key) {
        options.headers[key] = endpoint.headers[key];
      });
    }

    this.logger.info('Request:', options, '\nBody:', endpoint.body);
    var request;

    if (options.port == 443) {
        request = this.https.request(options);
    } else {
        request = http.request(options);
    }

    request.end(endpoint.body);

    // Keep an array of String or Buffers,
    // depending on content type (binary or JSON) of response
    var responseData = [];

    request.on('response', (response) => {
        var isBinary = response.headers['content-type'] === 'application/octet-stream';
        if (!isBinary) { response.setEncoding('utf8'); }

        response.on('data', (chunk) => {
          responseData.push(chunk);
        });

        response.on('end', () => {
            this.logger.info('response ended:', response.statusCode);
            if (callback) {
              if (isBinary) { responseData = Buffer.concat(responseData); }

              this.__parseResponse(
                response,
                responseData,
                endpoint.method,
                callback
              );
            }
        })
        response.on('close', (e) => {
            this.logger.error('problem with API request detailed stacktrace below ');
            this.logger.error(e);
            callback(e);
        });
    });
    request.on('error', (e) => {
        this.logger.error('problem with API request detailed stacktrace below ');
        this.logger.error(e);
        callback(e);
    });

  }

  __parseResponse(httpResponse, data, method, callback) {
    const isArrayOrBuffer = (data instanceof Array || data instanceof Buffer);
    if(!isArrayOrBuffer) {
      throw new Error('data should be of type Array or Buffer');
    }

    const status = httpResponse.statusCode;
    const headers = httpResponse.headers;

    let response = null;
    var error = null;

    try {
      if (status >= 500) {
        error = { message: 'Server Error', statusCode: status };
      } else if (httpResponse.headers['content-type'] === 'application/octet-stream') {
        response = data;
      } else if (status === 429) {
        // 429 does not return a parsable body
        if(!headers['retry-after']) {
          // retry based on allowed per second
          const retryAfterMillis = (method === 'POST'? 1000/2 : 1000/5);
          headers['retry-after'] = retryAfterMillis;
        }
        error = {body: data.join('')};
      } else if (status === 204) {
        response = null;
      } else if (status >= 400 || status < 200) {
        error = {body: JSON.parse(data.join('')), headers};
      } else if(method !== 'DELETE') {
        response = JSON.parse(data.join(''));
      } else {
        response = data;
      }
    } catch (parseError) {
      this.logger.error(parseError);
      this.logger.error('could not convert API response to JSON, above error is ignored and raw API response is returned to client');
      this.logger.error('Raw Error message from API ');
      this.logger.error(`"${data}"`);

      error = {
        message: "The API response could not be parsed.",
        body: data.join(''),
        parseError: parseError
      };
    }

    if(error) {
      error.statusCode = status;
      error.headers = headers;
    }

    if (typeof callback === "function"){
      callback(error, response);
    }
  }
}

export default HttpClient;

"use strict";

import fs from 'fs';
import JwtGenerator from './JwtGenerator';

/**
 * Right now only key/secret credentials are supported.
 * However, in time JWT will also be supported.
 * The `Credentials` object provides an abstraction to this.
 *
 * @param {string} apiKey - A Nexmo API Key
 * @param {string} apiSecret - A Nexmo API Secret
 * @param {string|Buffer} [privateKey] -  When a string value is passed it should
 *                        either represent the path to the private key, or the actual
 *                        private key in string format. If a Buffer is passed then
 *                        it should be the key read from the file system.
 */
class Credentials {
  constructor(apiKey, apiSecret, privateKey, applicationId) {
    this.apiKey = apiKey;
    this.apiSecret = apiSecret;

    this.privateKey = null;
    this.applicationId = applicationId;

    if(privateKey instanceof Buffer) {
      this.privateKey = privateKey;
    }
    else if(typeof privateKey === "string" &&
       privateKey.startsWith('-----BEGIN PRIVATE KEY-----')) {
      this.privateKey = new Buffer(privateKey);
    }
    else if(privateKey !== undefined) {
      if(!fs.existsSync(privateKey)) {
        throw new Error(`File "${privateKey}" not found.`)
      }
      this.privateKey = fs.readFileSync(privateKey);
    }

    /** @private */
    this._jwtGenerator = new JwtGenerator();
  }

  /**
   * Generate a Jwt using the Private Key in the Credentials.
   * By default the credentials.applicationId will be used when creating the token.
   * However, this can be overwritten.
   *
   * @param {string} [applicationId] an application ID to be used instead of the
   *                default Credentials.applicationId value.
   *
   * @returns {string} The generated JWT
   */
  generateJwt(applicationId = this.applicationId, privateKey = this.privateKey) {
    var claims = {application_id: applicationId};
    var token = this._jwtGenerator.generate(privateKey, claims);
    return token;
  }

  /**
   * @private
   * Used for testing purposes only.
   */
  _setJwtGenerator(generator) {
    this._jwtGenerator = generator;
  }

  /**
   * Ensures a credentials instance is used.
   *
   * Key/Secret credentials are only supported at present.
   */
  static parse(obj) {
    if(obj instanceof Credentials) {
      return obj;
    }
    else {
      return new Credentials(obj.apiKey, obj.apiSecret, obj.privateKey, obj.applicationId);
    }
  }
}

export default Credentials;

import uuid from 'uuid';
import jwt from 'jsonwebtoken';

class JwtGenerator {
	/**
	 * Generate a JSON Web Token (JWT).
	 *
	 * @param {Buffer} cert - the private key certificate to be used when signing
	 *		the claims.
	 * @param {Object} claims - additional claims to include within the generated
	 * 		JWT.
	 *
	 * @returns {String} the generated token
	 */
	generate(cert, claims = {}) {
		if(!cert instanceof Buffer) {
			throw new Error('cert must be of type Buffer');
		}
		if(typeof claims !== 'object') {
			throw new Error('claims must be of type object');
		}

		var toSign = {
			'iat': claims.issuedAt || parseInt(Date.now()/1000, 10),
			'jti': claims.jti || uuid.v1()
		};
		Object.keys(claims).forEach((key) => {
			toSign[key] = claims[key];
		});

		var token = jwt.sign(toSign, cert, {algorithm: 'RS256'});
		return token;
	}
}

module.exports = JwtGenerator;

